import React, { useState, useEffect } from "react";
import "../styles.css";
import { API } from "../backend";
import Base from "./Base";
import Card from "./Card";
import { getProducts } from "../admin/helper/adminapicall";
import { loadcart, removeItemFromCart } from "./helper/CartHelper";
import StripeCheckout from "./StripeCheckout";
import PayPalcheckout from "./Paypalcheckout";
import ImageHelper from "./helper/ImageHelper";
import { isAutheticated } from "../auth/helper";

const Cart = () => {
  const [products, setProducts] = useState([]);
  const [reload, setReload] = useState(false);
  const { user, token } = isAutheticated();

  console.log(user.address);

  useEffect(() => {
    setProducts(loadcart());
  }, [reload]);

  const ShowRemoveFromCart = (removeFromCart) => {
    return (
      removeFromCart && (
        <button
          onClick={() => {
            removeItemFromCart(products._id);
            setReload(!reload);
          }}
          className="btn btn-sm btn-outline-danger mt-2 mb-2"
        >
          Remove from cart
        </button>
      )
    );
  };

  const loadAllProduct = (products) => {
    return (
      <div>
        <h2>Shoping Cart</h2>
        {products.map((product, index) => (
          <Card
            className="p-100"
            key={index}
            product={product}
            removeFromCart={true}
            addtoCart={false}
            setReload={setReload}
            reload={reload}
          />
        ))}
      </div>
    );
  };

  const loadCheckout = () => {
    return (
      <div>
        <h2>This section is for checkout</h2>
      </div>
    );
  };

  return (
    <Base title="Cart Page" description="Ready to checkout">
      <div className="row">
        <div className="offset-1 col-4">
          {products.length > 0 ? loadAllProduct(products) : <h3>cart empty</h3>}
        </div>
        <div className="col-3">
        <h2>Shipping Address</h2>
          <div className="input-group mt-3">
  
            <div className="input-group-text">
              <input
                className="form-check-input mt-0"
                type="radio"
                
                aria-label="Radio button for following text input"
                style={{ width: "40px" }}
              />
            </div>
            <textarea
                class="form-control"
                rows="2"
                type="text"
                value={user.address}
                readOnly
              ></textarea>
          </div>
        </div>
        <div className="col-4">
        <p>
  <a class="btn btn-primary" data-bs-toggle="collapse" href="#multiCollapseExample1" role="button" aria-expanded="false" aria-controls="multiCollapseExample1">Toggle first element</a>
  <button class="btn btn-primary" type="button" data-bs-toggle="collapse" data-bs-target="#multiCollapseExample2" aria-expanded="false" aria-controls="multiCollapseExample2">Toggle second element</button>
  <button class="btn btn-primary" type="button" data-bs-toggle="collapse" data-bs-target=".multi-collapse" aria-expanded="false" aria-controls="multiCollapseExample1 multiCollapseExample2">Toggle both elements</button>
</p>
<div class="row">
  <div class="col">
    <div class="collapse multi-collapse" id="multiCollapseExample1">
      <div class="card card-body">
        Some placeholder content for the first collapse component of this multi-collapse example. This panel is hidden by default but revealed when the user activates the relevant trigger.
      </div>
    </div>
  </div>
  <div class="col">
    <div class="collapse multi-collapse" id="multiCollapseExample2">
      <div class="card card-body">
        Some placeholder content for the second collapse component of this multi-collapse example. This panel is hidden by default but revealed when the user activates the relevant trigger.
      </div>
    </div>
  </div>
</div>
          <StripeCheckout products={products} setReload={setReload} />

          <PayPalcheckout products={products} setReload={setReload} />
        </div>
      </div>
    </Base>
  );
};
export default Cart;
