import React,{useState,useEffect} from "react"
import { Redirect } from "react-router";
import { addItemToCart, removeItemFromCart } from "./helper/CartHelper";
import ImageHelper from "./helper/ImageHelper"

    const Card = ({ product}) => {

        const [redirect, setRedirect] = useState(false)
        const [count,setCount] = useState(product.count)

        const cartTitle = product ? product.name : "A photo from pexel"
        const cartDescription = product ? product.description : "Default description"
        const cartPrice = product ? product.price : "Default Price"


        const addToCart = () => {
          addItemToCart(product,() => setRedirect(true))
        }

        const getARedirect = () => {
          if(redirect){
            return <Redirect to="/cart" />
          }
        }


        const ShowAddToCart = (addtoCart) =>{
                return(
                    addtoCart && (
                        <button
                        onClick={addToCart}
                        className="btn btn-block btn-outline-success mt-2 mb-2"
                      >
                        Add to Cart
                      </button>
                    )
                )
        }
    
        const ShowRemoveFromCart = (removeFromCart) => {
            return(
                removeFromCart && (
                    <button
                    onClick={() => {
                      removeItemFromCart(product._id)
                      setReload(!reload)
                    }}
                    className="btn btn-block btn-outline-danger mt-2 mb-2"
                  >
                    Remove from cart
                  </button>
                )
            )
         
        }
        const cartComponent = ({ product, addtoCart = true, removeFromCart = true,setReload = f => f ,reload = undefined,}) => {
          return(
            <div class="card border-success mb-3">
            <div class="card-header bg-transparent border-success card-body text-success">
              Shoping Cart
            </div>
            <div class="card-body text-success">
            {getARedirect(redirect)}
            {/* {product.map((product, index) => ( */}
              <div>
              <div className="col-1"> {}</div>
                  <div className="col-3"> <ImageHelper product={product} /></div>
                  <div className="col-5">
                  {cartDescription}
                  </div>
                  <div className="col-3">
                  {ShowRemoveFromCart(removeFromCart)}
                  </div>
              
              </div>
              {/* )
          )
          } */}
            </div>
            <div class="card-footer bg-transparent border-success">
              Footer
            </div>
          </div>
          )
        } 
        return (
          <div className="card text-white bg-dark border border-info ">
            <div className="card-header lead">{cartTitle}</div>
            <div className="card-body">
            {getARedirect(redirect)}
                <ImageHelper product={product} />
              <p className="lead bg-success font-weight-normal text-wrap">
                {cartDescription}
              </p>
              <p className="btn btn-success rounded  btn-sm px-4">$ {cartPrice}</p>
              <div className="row">
                <div className="col-12">
                {ShowAddToCart(addtoCart)}
                </div>
                <div className="col-12">
                 {ShowRemoveFromCart(removeFromCart)}
                </div>
              </div>
            </div>
            <div>
              {cartComponent()}
            </div>
          </div>
        );
      };


export default Card
