import React,{useState,useEffect} from "react";
import { Link } from "react-router-dom";
import { isAutheticated } from "../auth/helper";
import Base from "../core/Base";
import { getCategories } from "./helper/adminapicall";


const UpdateCategory = () => {

    const  {user, token} = isAutheticated();

    const [values, setValues] = useState ({
        name:"",
        loading:"",
        error:"",
        createdProduct:"",
        getaRedirect:false,
        formData:""
    })

    const {name,loading,error,createdProduct,getaRedirect,formData} = values

    const preload = categoryId => {
        //  console.log(productid)
        getCategories(categoryId).then(data => {
            console.log(data)
            if (data.error){
               setValues({ ...values,error:data.error})
            }else{
              setValues({ ...values,
             name:data.name,
             formData:new FormData()
             })
      
            }
          })
        }

   const  UpdateCategoryfrom = () =>{
       return(
        <form>
        <div className="from-group">
          <p className="lead">Enter the Category</p>
          <input
            type="text"
            className="form-control my-3"
            onChange=""
            value={name}
            autoFocus
            required
          />
        </div>
        <button onClick={() => {}} className="btn btn-outline-info mb-2">
          Update Category
        </button>
      </form>
       )
     
    }

  return (
    <Base title="Update Category here!!!!" description="Welcome to Update Category" className="container bg-info p-4">
    <Link to="/admin/dashboard" className="btn btn-md btn-dark mb-3">Admin Home</Link>
    <div className="row bg-dark text-white rounded">
        <div className="col-md-8 offset-md-2">
           {UpdateCategoryfrom()}
        </div>
    </div>
</Base>
  );
};

export default UpdateCategory;
