import React,{useState,useEffect} from "react";
import "../styles.css";
import { API } from "../backend";
import Base from "./Base";
import Card from "./Card";
import { getProducts } from "../admin/helper/adminapicall";
import { loadcart } from "./helper/CartHelper";
import StripeCheckout from "./StripeCheckout";
import PayPalcheckout from "./Paypalcheckout";



 const Cart = () => {

    const [products, setProducts] = useState([])
    const [reload,setReload] = useState(false)

    useEffect(() =>{
        setProducts(loadcart())
    }, [reload])

    const loadAllProduct = (products) => {
        return(
            <div>
                <h2>This section is load product</h2>
                {products.map((product,index) => (
                    <Card 
                    key={index}
                    product={product}
                    removeFromCart={true}
                    addtoCart={false}
                    setReload={setReload}
                    reload={reload}
                    />
                ))}
            </div>
        )
    }

    const loadCheckout = () => {
        return(
            <div>
                <h2>This section is for checkout</h2>
            </div>
        )
    }

  return (
    <Base title="Cart Page" description="Ready to checkout">
      <div className="row">
       <div className="offset-1 col-6">{products.length > 0 ? (loadAllProduct(products)): (<h3>No products in cart</h3>)}</div>
       <div className="col-4">
        <StripeCheckout
        products={products}
        setReload={setReload}
        />
        
    <PayPalcheckout 
        products={products} setReload={setReload}
    />
       </div>
      </div>
    </Base>
  );
}
 export default Cart
