import DropIn from "braintree-web-drop-in-react"
import React,{useState,useEffect} from "react"
import { Link } from "react-router-dom"
import { isAutheticated } from "../auth/helper"
import { cartEmpty, loadcart } from "./helper/CartHelper"
import { createOrder } from "./helper/OrderHelper"
import { getmeToken, processPayment } from "./helper/PaypalHelper"


const PayPalcheckout = ({products,setReload = f => f,reload = undefined}) => {
    
    const [info,setInfo] = useState({
        loading:false,
        success:false,
        clientToken:null,
        error:"",
        instance:{}
    })

    const userId = isAutheticated() &&  isAutheticated().user._id
    const token = isAutheticated() && isAutheticated().token
    
     
    const getToken = (userId,token) => {
      
        getmeToken(userId,token).then(info => {
            console.log("INFORMATION",info)
            if(info.error){
                setInfo({...info,error:info.error})
            }else{
                const clientToken = info.clientToken
                //console.log("clientToken",setInfo)
                setInfo({clientToken})
            }
        })
    }


    const showbtdropIn = () => {
        return(
            <div>
                {info.clientToken !==null && products.length > 0 ? (
                    <div>
                        <DropIn 
                            options={{ authorization:info.clientToken}}
                            onInstance={instance => (info.instance = instance)}
                        />
                        <button className="btn btn-block btn-outline-success" onClick={() => {}}>Buy</button>
                    </div>
                ) : (<h3>Please login or add something to cart</h3>)}
            </div>
        )
    }


    useEffect(() => {
        getToken(userId,token)
    },[])

    const onPurchase = () => {
        setInfo({loading:true})
        let nonce
        let getNonce = info.instance
        .requestPaymentMethod()
        .then(data => {
            nonce = data.nonce
            const paymentData = {
                paymentMethodNonce : nonce,
                amount:getAmount()
            }
            processPayment(userId,token,paymentData)
            .then(response => {
                setInfo({...info,success:response.success,loading:false})
            })
            .catch(error => {
                setInfo({loading:false, success:false})
            })
        })
    }

    const getAmount = () => {
        let amount = 0
        products.map(p => {
            amount = amount + p.price
        })
        return amount
    }

    return(
        <div>
          <h3>Your bill is{getAmount()}</h3>
        {showbtdropIn()}
        </div>
      
    )
}

export default PayPalcheckout
