import React, { useState, useEffect } from "react";
import "../styles.css";
import { API } from "../backend";
import Base from "./Base";
import Card from "./Card";
import { getProducts } from "../admin/helper/adminapicall";
import { loadcart, removeItemFromCart } from "./helper/CartHelper";
import StripeCheckout from "./StripeCheckout";
import PayPalcheckout from "./Paypalcheckout";
import ImageHelper from "./helper/ImageHelper"

const Cart = ({removeFromCart = false}) => {
  const [products, setProducts] = useState([]);
  const [reload, setReload] = useState(false);
  console.log(products)
  useEffect(() => {
    setProducts(loadcart());
  }, [reload]);

  const ShowRemoveFromCart = () => {
    return(
       
            <button
            onClick={() => {
              removeItemFromCart(products._id)
              setReload(!reload)
            }}
            className="btn btn-sm btn-outline-danger mt-2 mb-2"
          >
            Remove from cart
          </button>
        
    )
 
}


  const loadAllProduct = (products) => {
    return (
      <div>
        <h2>This section is load product</h2>
        
    
            <div class="card border-success mb-3">
              <div class="card-header bg-transparent border-success card-body text-success">
                Shoping Cart
              </div>
              <div class="card-body text-success">
              {products.map((product, index) => (
                <div className="row mt-2">
                <div className="col-1"> {index + 1}</div>
                    <div className="col-3"> <ImageHelper product={product} /></div>
                    <div className="col-5">
                    <p>{product.name}</p>
                    <p>{product.description}</p>
                    </div>
                    <div className="col-3">
                        {product.price}
                        {ShowRemoveFromCart()}
                    </div>
                
                </div>
                )
            )
            }
              </div>
              <div class="card-footer bg-transparent border-success">
                Footer
              </div>
            </div>
           
      </div>
    );
  };

  const loadCheckout = () => {
    return (
      <div>
        <h2>This section is for checkout</h2>
      </div>
    );
  };

  return (
    <Base title="Cart Page" description="Ready to checkout">
      <div className="row">
        <div className="offset-1 col-6">
          {products.length > 0 ? (
            loadAllProduct(products)
          ) : (
     <h3>cart empty</h3>
          )}
        </div>
        <div className="col-4">
          <StripeCheckout products={products} setReload={setReload} />

          <PayPalcheckout products={products} setReload={setReload} />
        </div>
      </div>
    </Base>
  );
};
export default Cart;
