import React,{useState,useEffect} from "react"
import { Link } from "react-router-dom"
import Base from "../core/Base"
import {getCategories} from "../admin/helper/adminapicall"
import { isAutheticated } from "../auth/helper"



const AddProduct = () => {

const  {user, token} = isAutheticated();

const [values, setValues] = useState ({
    name:"",
    description:"",
    price:"",
    stock:"",
    photo:"",
    categories:[],
    category:"",
    loading:"",
    error:"",
    createdProduct:"",
    getaRedirect:false,
    formData:""
})

    const {name,description,price,stock,photo,categories,category,loading,error,createdProduct,getaRedirect,formData} = values

   const preload = () => {
     getCategories().then(data => {
       console.log(data)
       if (data.error){
          setValues({ ...values,error:data.error})
       }else{
         setValues({ ...values,categories:data ,formData:new formData()})
         console.log(categories)
       }
     })
   }
    
    useEffect(() => {
      preload()
    },[])

    const onSubmit = () => {
        //
    }

    const handleChange = name => event =>  {
        //
    } 
    const createProductForm = () => (
        <form >
          <span>Post photo</span>
          <div className="form-group">
            <label className="btn btn-block btn-success">
              <input
                onChange={handleChange("photo")}
                type="file"
                name="photo"
                accept="image"
                placeholder="choose a file"
              />
            </label>
          </div>
          <div className="form-group">
            <input
              onChange={handleChange("name")}
              name="photo"
              className="form-control"
              placeholder="Name"
              value={name}
            />
          </div>
          <div className="form-group">
            <textarea
              onChange={handleChange("description")}
              name="photo"
              className="form-control"
              placeholder="Description"
              value={description}
            />
          </div>
          <div className="form-group">
            <input
              onChange={handleChange("price")}
              type="number"
              className="form-control"
              placeholder="Price"
              value={price}
            />
          </div>
          <div className="form-group">
            <select
              onChange={handleChange("category")}
              className="form-control"
              placeholder="Category"
            >
              <option>Select</option>
              {categories && 
              categories.map((cate,index) => (
                <option key={index} value={cate._id}>{cate.name}</option>
              ))}
            </select>
          </div>
          <div className="form-group">
            <input
              onChange={handleChange("quantity")}
              type="number"
              className="form-control"
              placeholder="Quantity"
              value={stock}
            />
          </div>
          
          <button type="submit" onClick={onSubmit} className="btn btn-outline-success">
            Create Product
          </button>
        </form>
      );

    return (
        <Base title="Add Product here!!!!" description="Welcome to product Creation" className="container bg-info p-4">
            <Link to="/admin/dashboard" className="btn btn-md btn-dark mb-3">Admin Home</Link>
            <div className="row bg-dark text-white rounded">
                <div className="col-md-8 offset-md-2">
                    {createProductForm()}
                </div>
            </div>
        </Base>
    )
}

export default AddProduct
